import 'package:flutter/material.dart';
import 'package:hr_app/src/models/member.dart';

class EmployeeInfoScreen extends StatelessWidget {
  final Member member;
  const EmployeeInfoScreen({
    Key? key,
    required this.member,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return DefaultTabController(
      length: 7,
      child: Scaffold(
        appBar: AppBar(
          title: const Text('Employee Information'),
        ),
        body: Column(
          children: [
            const Center(
                child: Icon(
              Icons.person,
              size: 120,
            )),
            Text(
              member.name,
              style: const TextStyle(
                fontSize: 28,
                fontWeight: FontWeight.bold,
              ),
            ),
            const TabBar(
              isScrollable: true,
              tabs: [
                Tab(text: 'Personal Info'),
                Tab(text: 'Employment Info'),
                Tab(text: 'Contact Info'),
                Tab(text: 'Attendance'),
                Tab(text: 'Benefits'),
                Tab(text: 'Payroll'),
                Tab(text: 'Leaves'),
              ],
            ),
            Expanded(
              child: TabBarView(
                children: [
                  PersonalInfoTab(member: member),
                  EmploymentInfoTab(member: member),
                  ContactInfoTab(member: member),
                  const Placeholder(),
                  const Placeholder(),
                  const Placeholder(),
                  const Placeholder(),
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }
}

