import 'package:flutter/material.dart';
import 'package:hr_app/src/models/member.dart';
import 'package:hr_app/src/widgets/data_tile.dart';

class ContactInfoTab extends StatefulWidget {
  final Member member;
  const ContactInfoTab({
    Key? key,
    required this.member,
  }) : super(key: key);

  @override
  State<ContactInfoTab> createState() => _ContactInfoTabState();
}

class _ContactInfoTabState extends State<ContactInfoTab> {
  bool isCollapsed = true;
  @override
  Widget build(BuildContext context) {
    return Container(
      padding: const EdgeInsets.all(15),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: <Widget>[
          ListTile(
            title: const Text(
              'Contact information',
              style: TextStyle(fontSize: 22, fontWeight: FontWeight.bold),
            ),
            trailing: IconButton(
                onPressed: () {
                  setState(() {
                    isCollapsed = !isCollapsed;
                  });
                },
                icon:
                    Icon(isCollapsed ? Icons.expand_more : Icons.expand_less)),
          ),
          if (!isCollapsed)
            Column(
              children: [
                DataTile
            (parameter: 'Address', data: widget.member.address),
                const SizedBox(height: 10),
                DataTile(
                    parameter: 'Emergency contact name',
                    data: widget.member.emergencyContactName),
                const SizedBox(height: 10),
                Text(
                  'Emergency Contact Relationship: ${widget.member.emergencyContactRelationship}',
                  style: const TextStyle(
                      fontSize: 18, fontWeight: FontWeight.bold),
                ),
                const SizedBox(height: 10),
                Text(
                  'Emergency Contact Number: ${widget.member.emergencyContactNumber}',
                  style: const TextStyle(
                      fontSize: 18, fontWeight: FontWeight.bold),
                ),
                const SizedBox(height: 10),
                Text(
                  'Alternate Number: ${widget.member.alternateNumber}',
                  style: const TextStyle(
                      fontSize: 18, fontWeight: FontWeight.bold),
                ),
              ],
            )
        ],
      ),
    );
  }
}
